INSERT INTO `commitments` (`id`, `title`, `guarantee`, `status`) VALUES
(1, 'وجه التزام : در صورت تخلف سرمایه پذیر در اجرای هر یک از تعهدات قراردادی بیش از 45 روز کاری', '', 4),
(2, 'چک و سفته به میزان 120 درصد مبلغ  کل سرمایه گذاری', '', 4),
(3, 'نقل و انتقال سهام به سرمایه گذار و انجام تشریفات آن', '', 4),
(4, 'انجام شاخص های کلیدی عملکرد و ارائه طرح کسب و کار', '', 4),
(5, 'ثبت مطالبات سرمایه گذار در قالب افزایش سرمایه', '', 4),
(6, 'عدم تاسیس شرکت یا همکاری با شرکت های دیگر با فعالیت مشابه', '', 4),
(7, 'به کار گیری افراد متخصص و خبره در زمینه فعالیت شرکت/ منع فعالیت مضاعف برای کارکنان کلیدی / اخذ تعهد نامه محرمانگی و منع رقابت از کارکنان و همکاران', '', 4),
(8, 'رعایت اصول و استانداردهای مالی و حسابداری توسط شرکت', '', 4),
(9, 'رعایت کلیه قوانین و مقررات موضوع کشور', '', 4),
(10, 'ارائه اطلاعات و جزئیات لازم به سرمایه گذار', '', 4),
(11, 'ارائه دفاتر و صورت های مالی به نماینده سرمایه گذار ظرف یک ماه پس از پایان سال مالی', '', 4),
(12, 'اطلاع گردش های مالی ظرف 3 روز به نماینده سرمایه گذار.', '', 4),
(13, 'برداشت های بالای 50 میلیون تومان از حساب شرکت باید با تایید نماینده هیئت مدیره سرمایه گذار انجام شود', '', 4),
(14, 'ارائه برنامه بودجه ماهانه به نماینده سرمایه گذار', '', 4),
(15, 'همکاری سرمایه پذیر با ناظر اجرای قرارداد و ارائه اطلاعات لازم به وی', '', 4),
(16, 'اجرا، پیگیری و گزارش دهی نسبت به کلیه تصمیماتی که در ارکان مدیریتی شرکت از جمله هیأت مدیره اتخاذ میگردد بر عهده سرمایه پذیر است', '', 4),
(17, 'وثیقه نمودن رسمی سهام در دفتر خانه اسناد رسمی و دفتر ثبت سهام شرکت به نفع سرمایه گذار توسط کارآفرین', '', 4),
(18, 'اعطا وکالتنامه رسمی بلاعزل از سوی سرمایه پذیر به سرمایه‌گذار برای نقل و انتقال سهام', '', 4),
(19, 'عدم نقل و انتقال سهام توسط کارآفرین بدون اذن سرمایه گذار', '', 4),
(20, 'عدم برداشت سود سهام توسط سهامداران بدون تایید هیات مدیره و در اختیار مدیر عامل قراردادن آن در طول مدت ارزش آفرینی', '', 4),
(21, 'صرف و استفاده از سرمایه، سرمایه گذار و اموال شرکت در راستای موضوعات شرکت و قرارداد و تصمیمات هیات مدیره صرف گردد', '', 4),
(22, 'کلیه منافع،  درآمد ها و به طور کلیه هر نوع ورودی مبلغ به هر نحو و نوع که باشد، می بایست از طریق واریز به حساب های رسمی شرکت باشد', '', 4),
(23, 'حفظ محرمانگی اطلاعات توسط سرمایه پذیران و مسئولیت کارکنان آن ها بر عهده خود سرمایه پذیران', '', 4),
(24, 'انتقال دارایی های معنوی و مادی به شرکت اگر به نام اشخاص دیگر طرف قرارداد باشد', '', 4),
(25, 'اصلاح اساسنامه و تطابق آن با قرارداد سرمایه گذاری', '', 4),
(26, 'اعلام تغییر نشانی شرکت ظرف یک هفته به نماینده سرمایه گذار', '', 4),
(27, 'ابلاغ مربوط به فسخ و موارد مهم باید با اظهارنامه قانونی باشد', '', 4),
(28, 'پرداخت اقساط سرمایه گذاری بر اساس برنامه بودجه هیات مدیره', '', 4);
